package com.example.chat.chat; import jakarta.persistence.*; import java.time.*; @Entity @Table(name="chat_room") public class ChatRoomEntity{ @Id @GeneratedValue(strategy=GenerationType.IDENTITY) Long id; @Column(nullable=false,unique=true) String code; @Column(nullable=false) String title; @Column(nullable=false) long nextSeq; @Column(nullable=false) Instant createdAt=Instant.now(); public Long getId(){return id;} public String getCode(){return code;} public void setCode(String v){code=v;} public String getTitle(){return title;} public void setTitle(String v){title=v;} public long getNextSeq(){return nextSeq;} public void setNextSeq(long v){nextSeq=v;} }