package com.example.chat.user; import jakarta.persistence.*; import java.time.*; @Entity @Table(name="app_user") public class UserEntity{ @Id @GeneratedValue(strategy=GenerationType.IDENTITY) Long id; @Column(nullable=false,unique=true) String email; @Column(nullable=false) String passwordHash; @Column(nullable=false) String displayName; @Column(nullable=false) Instant createdAt=Instant.now(); public Long getId(){return id;} public String getEmail(){return email;} public void setEmail(String v){email=v;} public String getPasswordHash(){return passwordHash;} public void setPasswordHash(String v){passwordHash=v;} public String getDisplayName(){return displayName;} public void setDisplayName(String v){displayName=v;} }